import React, { useState } from 'react';
import "./signUp.css";
import { Link } from "react-router-dom";

function SignUp() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };


  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleConfirmPasswordChange = (e) => {
    setConfirmPassword(e.target.value);
  };
  const isFormValid = () => {
    // Regular expression for a simple email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    // Check if username is not empty
    if (!username.trim()) {
      return { isValid: false, message: 'Username is required' };
    }

    // Check if the email is a valid email address
    if (!emailRegex.test(username.trim())) {
      return { isValid: false, message: 'Please enter a valid email address' };
    }

    // Check if password is at least 8 characters long
    if (password.length < 8) {
      return { isValid: false, message: 'Password must be at least 8 characters long' };
    }

    // Check if confirm password matches the password
    if (confirmPassword !== password) {
      return { isValid: false, message: 'Passwords do not match' };
    }

    // If all checks pass, the form is valid
    return { isValid: true, message: '' };
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isFormValid()) {
      // Make a request to the server with the data
      // Example using fetch:
      fetch('your-server-endpoint', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      })
        .then(response => response.json())
        .then(data => {
          // Handle the server response
        })
        .catch(error => {
          console.error('Error submitting data:', error);
        });
    } else {
      // Handle invalid form submission
    }
  };



  return (
    <div className="signup" >
      <span className="heading greet">Welcome To </span>
      <span className="h1 heading "> Project Of Guvi Assienment</span>

      <span className=" heading emailtxt">Enter the email</span>
      <form className="form" onSubmit={handleSubmit}>
        <input
          className="form__inset"
          typeof="text"
          placeholder="Email"
          name="email"
          onChange={handleUsernameChange}
        />
        <label className=" heading emailtxt">Enter the Password</label>

        <input
          className="form__inset"
          typeof="text"
          placeholder="Password"
          name="email"
          value={password}
          onChange={handlePasswordChange} 
        />
        <span className=" heading emailtxt">Enter the Conform Password</span>

        <input
          className="form__inset"
          typeof="text"
          placeholder="Password"
          name="email"
          value={confirmPassword} onChange={handleConfirmPasswordChange}
        />
        <button
          type="submit"
          onClick={() => {
            console.log("thi is the sign up fun");
          }}
          className="login-butt"
        >
          {" "}
          SignUp
        </button>
      </form>
      <span className=" heading emailtxt">
        If You Have Already Account Please <Link to={"/login"}> login</Link>
      </span>
    </div>
  );
}

export default SignUp;
