import React from "react";
import "./signUp.css";
import { Link } from "react-router-dom";
import EmployeeComponent from "../Components/TextField";
function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const isFormValid = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!email.trim()) {
      setError('Email is required');
      return false;
    }

    if (!emailRegex.test(email.trim())) {
      setError('Please enter a valid email address');
      return false;
    }

    if (password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }

    setError('');
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isFormValid()) {
      // Make a request to the server for login
      // ...

      // Clear the form or perform other actions
      setEmail('');
      setPassword('');
    }
  };


  return (
    <div className="signup">
      <span className="heading greet">Login </span>
      {/* <span className="h1 heading "> Project Of Guvi Assienment</span> */}

      <span className=" heading emailtxt">Enter the email</span>

      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input className="form__inset" type="text" value={email} onChange={handleEmailChange} />
        {error && <p style={{ color: 'red' }}>{error}</p>}

        <label>Password:</label>
        <input className="form__inset" type="password" value={password} onChange={handlePasswordChange} />

        <button type="submit" className="login-butt">Login</button>
      </form>

    {/* <button className="login-butt"> Login</button> */}
      {/* <input
        className="form__inset"
        typeof="text"
        placeholder="Password"
        name="email"
      /> */}
      <span className=" heading emailtxt">
        If You Don't Have An Account Please <Link to={"/signup"}> SignUp</Link>
        {/* <Outlet /> */}
      </span>
    </div>
  );
}

export default Login;
